import React from "react";
import { useHistory } from "react-router-dom";
import { Container, MenuCard, MenuGrid, Title } from "./styles";
import { FaBox, FaClipboardList } from "react-icons/fa";

export const Menu = () => {
  const history = useHistory();

  return (
    <Container>
      <Title>Menu Principal</Title>
      <MenuGrid>
        <MenuCard onClick={() => history.push("/produtos")}>
          <FaBox size={40} />
          <h3>Produtos</h3>
          <p>Gerenciar produtos do sistema</p>
        </MenuCard>

        <MenuCard onClick={() => history.push("/draft")}>
          <FaClipboardList size={40} />
          <h3>Draft</h3>
          <p>Área em desenvolvimento</p>
        </MenuCard>
      </MenuGrid>
    </Container>
  );
};
