import React, { useState, useEffect } from "react";
import { Link, useHistory } from "react-router-dom";
import { useAuth } from "../../contexts/AuthContext";

import {
  Container,
  ConteudoTitulo,
  BotaoAcao,
  ButtonSuccess,
  ButtonDanger,
  Table,
  AlertDanger,
} from "./styles";

export const Home = () => {
  const [produtos, setProdutos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { signOut } = useAuth();
  const history = useHistory();

  useEffect(() => {
    const fetchProdutos = async () => {
      try {
        const response = await fetch("http://localhost:8000/produtos");
        const data = await response.json();

        if (data.erro) {
          setError(data.mensagem);
          setProdutos([]);
        } else {
          setProdutos(data.produtos || []);
        }
      } catch (err) {
        setError("Erro ao carregar produtos: " + err.message);
        setProdutos([]);
      } finally {
        setLoading(false);
      }
    };

    fetchProdutos();
  }, []);

  const handleDelete = async (id) => {
    if (window.confirm("Deseja realmente excluir este produto?")) {
      try {
        const response = await fetch(`http://localhost:8000/produtos/${id}`, {
          method: "DELETE",
        });
        const data = await response.json();

        if (data.erro) {
          throw new Error(data.mensagem);
        }

        setProdutos(produtos.filter((produto) => produto.id !== id));
      } catch (err) {
        setError("Erro ao excluir produto: " + err.message);
      }
    }
  };

  const handleLogout = () => {
    signOut();
    history.push("/login");
  };

  if (loading) {
    return (
      <Container>
        <div>Carregando...</div>
      </Container>
    );
  }

  return (
    <Container>
      <ConteudoTitulo>
        <h1>Listar Produtos</h1>
        <div>
          <BotaoAcao onClick={handleLogout} style={{ marginRight: "10px" }}>
            Sair
          </BotaoAcao>
          <Link to="/cadastrar">
            <ButtonSuccess>Cadastrar</ButtonSuccess>
          </Link>
        </div>
      </ConteudoTitulo>

      {error && <AlertDanger>{error}</AlertDanger>}

      <Table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Título</th>
            <th>Descrição</th>
            <th>Ações</th>
          </tr>
        </thead>
        <tbody>
          {produtos.length > 0 ? (
            produtos.map((produto) => (
              <tr key={produto.id}>
                <td>{produto.id}</td>
                <td>{produto.titulo}</td>
                <td>{produto.descricao}</td>
                <td>
                  <Link to={`/visualizar/${produto.id}`}>
                    <ButtonSuccess>Visualizar</ButtonSuccess>
                  </Link>{" "}
                  <Link to={`/editar/${produto.id}`}>
                    <ButtonSuccess>Editar</ButtonSuccess>
                  </Link>{" "}
                  <ButtonDanger onClick={() => handleDelete(produto.id)}>
                    Deletar
                  </ButtonDanger>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="4" style={{ textAlign: "center" }}>
                Nenhum produto cadastrado
              </td>
            </tr>
          )}
        </tbody>
      </Table>
    </Container>
  );
};

export default Home;
