import React, { useState, useEffect } from "react";
import { useHistory } from "react-router-dom";
import {
  Container,
  ConteudoTitulo,
  Titulo,
  ConteudoForm,
  Form,
  Label,
  Input,
  BotaoAcao,
  AlertSuccess,
  AlertDanger,
  Select,
} from "./styles";
import { useAuth } from "../../contexts/AuthContext";

export const EditarPerfil = () => {
  const history = useHistory();
  const { user } = useAuth();

  const [usuario, setUsuario] = useState({
    nome: "",
    email: "",
    nivel: "",
    senha_atual: "",
    nova_senha: "",
    confirmar_senha: "",
  });

  const [status, setStatus] = useState({
    type: "",
    mensagem: "",
  });

  useEffect(() => {
    const carregarUsuario = async () => {
      try {
        const token = localStorage.getItem("token");
        if (!token) {
          throw new Error("Token não encontrado");
        }

        const response = await fetch("http://localhost:8000/usuarios/perfil", {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
          credentials: "include",
        });

        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }

        const data = await response.json();

        if (data.erro) {
          setStatus({
            type: "erro",
            mensagem: data.mensagem,
          });
        } else {
          setUsuario((prevState) => ({
            ...prevState,
            nome: data.nome,
            email: data.email,
            nivel: data.nivel,
          }));
        }
      } catch (err) {
        setStatus({
          type: "erro",
          mensagem: "Erro ao carregar dados do usuário: " + err.message,
        });
        setTimeout(() => {
          history.push("/login");
        }, 2000);
      }
    };

    carregarUsuario();
  }, [history]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (usuario.nova_senha && usuario.nova_senha !== usuario.confirmar_senha) {
      setStatus({
        type: "erro",
        mensagem: "As senhas não conferem!",
      });
      return;
    }

    try {
      const response = await fetch("http://localhost:8000/usuarios/perfil", {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
        body: JSON.stringify({
          nome: usuario.nome,
          email: usuario.email,
          nivel: usuario.nivel,
          senha_atual: usuario.senha_atual,
          nova_senha: usuario.nova_senha,
        }),
      });

      const data = await response.json();

      if (data.erro) {
        setStatus({
          type: "erro",
          mensagem: data.mensagem,
        });
      } else {
        setStatus({
          type: "success",
          mensagem: "Perfil atualizado com sucesso!",
        });
        setTimeout(() => {
          history.push("/menu");
        }, 2000);
      }
    } catch (err) {
      setStatus({
        type: "erro",
        mensagem: "Erro ao atualizar perfil: " + err.message,
      });
    }
  };

  return (
    <Container>
      <ConteudoTitulo>
        <Titulo>Editar Perfil</Titulo>
        <BotaoAcao onClick={() => history.push("/menu")}>Voltar</BotaoAcao>
      </ConteudoTitulo>

      {status.type === "erro" && <AlertDanger>{status.mensagem}</AlertDanger>}
      {status.type === "success" && (
        <AlertSuccess>{status.mensagem}</AlertSuccess>
      )}

      <ConteudoForm>
        <Form onSubmit={handleSubmit}>
          <div>
            <Label>Nome</Label>
            <Input
              type="text"
              value={usuario.nome}
              onChange={(e) => setUsuario({ ...usuario, nome: e.target.value })}
            />
          </div>

          <div>
            <Label>Email</Label>
            <Input
              type="email"
              value={usuario.email}
              readOnly
              style={{ backgroundColor: "#f5f5f5" }}
            />
          </div>

          <div>
            <Label>Nível de Acesso</Label>
            <Select
              value={usuario.nivel}
              onChange={(e) =>
                setUsuario({ ...usuario, nivel: e.target.value })
              }
            >
              <option value="">Selecione um nível</option>
              <option value="admin">Administrador</option>
              <option value="user">Usuário</option>
            </Select>
          </div>

          <div>
            <Label>Senha Atual</Label>
            <Input
              type="password"
              value={usuario.senha_atual}
              onChange={(e) =>
                setUsuario({ ...usuario, senha_atual: e.target.value })
              }
            />
          </div>

          <div>
            <Label>Nova Senha</Label>
            <Input
              type="password"
              value={usuario.nova_senha}
              onChange={(e) =>
                setUsuario({ ...usuario, nova_senha: e.target.value })
              }
            />
          </div>

          <div>
            <Label>Confirmar Nova Senha</Label>
            <Input
              type="password"
              value={usuario.confirmar_senha}
              onChange={(e) =>
                setUsuario({ ...usuario, confirmar_senha: e.target.value })
              }
            />
          </div>

          <div className="button-container">
            <BotaoAcao type="submit">Atualizar</BotaoAcao>
          </div>
        </Form>
      </ConteudoForm>
    </Container>
  );
};
