// Importação das dependências necessárias
import React, { useState } from "react";
import { Link, useHistory } from "react-router-dom";
import { useAuth } from "../../contexts/AuthContext";

// Importação dos componentes estilizados
import {
  Container,
  ConteudoForm,
  Form,
  Label,
  Input,
  ButtonSuccess,
  AlertSuccess,
  AlertDanger,
  LinkContainer,
} from "./styles";

export const Login = () => {
  // Hook para navegação entre páginas
  const history = useHistory();
  // Hook personalizado para autenticação
  const { signIn } = useAuth();

  // Estado para armazenar as credenciais do usuário
  const [credentials, setCredentials] = useState({
    email: "",
    senha: "",
  });

  // Estado para controlar mensagens de status/erro
  const [status, setStatus] = useState({
    type: "",
    mensagem: "",
  });

  // Função que atualiza o estado das credenciais conforme o usuário digita
  const valorInput = (e) =>
    setCredentials({
      ...credentials,
      [e.target.name]: e.target.value,
    });

  // Função que gerencia o processo de login
  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch("http://localhost:8000/login", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          email: credentials.email,
          senha: credentials.senha,
        }),
      });

      const data = await response.json();
      console.log("Resposta do login:", data);

      if (data && !data.erro) {
        // Salva os dados completos do usuário
        localStorage.setItem(
          "@Authuser",
          JSON.stringify([
            {
              nome: data.usuario.nome,
              email: credentials.email,
              nivel_acesso: data.usuario.nivel_acesso,
            },
          ])
        );

        console.log(
          "Dados salvos no localStorage:",
          localStorage.getItem("@Authuser")
        ); // Debug

        history.push("/");
      } else {
        setStatus({
          type: "erro",
          mensagem: data.mensagem,
        });
      }
    } catch (err) {
      console.error("Erro no login:", err);
      setStatus({
        type: "erro",
        mensagem: "Erro: " + err.message,
      });
    }
  };

  // Renderização do componente
  return (
    <Container>
      <ConteudoForm>
        <h1>Login</h1>

        {/* Exibe mensagem de erro se houver */}
        {status.type === "erro" && <AlertDanger>{status.mensagem}</AlertDanger>}

        {/* Formulário de login */}
        <Form onSubmit={handleLogin}>
          <Label>Email: </Label>
          <Input
            type="email"
            name="email"
            placeholder="Digite seu email"
            onChange={valorInput}
          />

          <Label>Senha: </Label>
          <Input
            type="password"
            name="senha"
            placeholder="Digite sua senha"
            onChange={valorInput}
          />

          <ButtonSuccess type="submit">Acessar</ButtonSuccess>

          <LinkContainer>
            <Link to="/CadastrarUsuario">Criar Usuário</Link>
            <Link to="/alterar-senha">Alterar Senha</Link>
          </LinkContainer>
        </Form>
      </ConteudoForm>
    </Container>
  );
};
