import React, { useState, useEffect } from "react";
import { useHistory, useParams } from "react-router-dom";
import {
  Container,
  Button,
  Form,
  FormGroup,
  Label,
  Input,
  Alert,
} from "./styles";

export const Editar = () => {
  const history = useHistory();
  const { id } = useParams();

  const [produto, setProduto] = useState({
    titulo: "",
    descricao: "",
  });

  const [status, setStatus] = useState({
    type: "",
    mensagem: "",
  });

  const getProduto = async () => {
    try {
      const response = await fetch(`http://localhost:8000/produtos/${id}`);

      if (!response.ok) {
        throw new Error(`Erro HTTP: ${response.status}`);
      }

      const data = await response.json();

      if (data.erro) {
        throw new Error(data.mensagem);
      }

      setProduto(data.produto);
    } catch (err) {
      setStatus({
        type: "erro",
        mensagem: "Erro ao carregar o produto: " + err.message,
      });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch(`http://localhost:8000/produtos/${id}`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(produto),
      });

      if (!response.ok) {
        throw new Error(`Erro HTTP: ${response.status}`);
      }

      const data = await response.json();

      if (data.erro) {
        throw new Error(data.mensagem);
      }

      setStatus({
        type: "success",
        mensagem: data.mensagem,
      });

      // Redireciona após 2 segundos
      setTimeout(() => {
        history.push("/");
      }, 2000);
    } catch (err) {
      setStatus({
        type: "erro",
        mensagem: "Erro ao atualizar o produto: " + err.message,
      });
    }
  };

  useEffect(() => {
    getProduto();
  }, [id]);

  return (
    <Container>
      <div className="d-flex">
        <h1>Editar Produto</h1>
      </div>

      {status.type === "erro" ? (
        <Alert color="danger">{status.mensagem}</Alert>
      ) : status.type === "success" ? (
        <Alert color="success">{status.mensagem}</Alert>
      ) : (
        ""
      )}

      <Form onSubmit={handleSubmit}>
        <FormGroup>
          <Label>Título</Label>
          <Input
            type="text"
            name="titulo"
            placeholder="Título do produto"
            value={produto.titulo}
            onChange={(e) => setProduto({ ...produto, titulo: e.target.value })}
          />
        </FormGroup>

        <FormGroup>
          <Label>Descrição</Label>
          <Input
            type="text"
            name="descricao"
            placeholder="Descrição do produto"
            value={produto.descricao}
            onChange={(e) =>
              setProduto({ ...produto, descricao: e.target.value })
            }
          />
        </FormGroup>

        <Button type="submit">Salvar</Button>
      </Form>
    </Container>
  );
};
