import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { useAuth } from "../../contexts/AuthContext";
import { useHistory } from "react-router-dom";

import {
  Container,
  ConteudoTitulo,
  BotaoAcao,
  ButtonSuccess,
  Table,
  Titulo,
  ButtonPrimary,
  ButtonWarning,
  ButtonDanger,
  AlertSuccess,
  AlertDanger,
  HeaderContent,
} from "./styles";

export const Home = () => {
  const history = useHistory();
  const { signOut, user } = useAuth();
  const [data, setData] = useState([]);
  const [status, setStatus] = useState({
    type: "",
    mensagem: "",
  });

  const handleLogout = () => {
    signOut();
    history.push("/login");
  };

  const getProdutos = async () => {
    try {
      const response = await fetch("http://localhost:8000/produtos");

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const responseJson = await response.json();

      if (responseJson.erro) {
        throw new Error(responseJson.mensagem);
      }

      setData(responseJson.records);
    } catch (err) {
      setStatus({
        type: "erro",
        mensagem: "Erro ao carregar produtos: " + err.message,
      });
      setData([]);
    }
  };

  const apagarProduto = async (idProduto) => {
    try {
      const response = await fetch(
        "http://localhost:8000/produtos?id=" + idProduto,
        {
          method: "DELETE",
        }
      );
      const responseJson = await response.json();

      if (responseJson.erro) {
        setStatus({
          type: "erro",
          mensagem: responseJson.mensagem,
        });
      } else {
        setStatus({
          type: "success",
          mensagem: responseJson.mensagem,
        });
        getProdutos();
      }
    } catch (err) {
      setStatus({
        type: "erro",
        mensagem: "Erro: Produto não apagado com sucesso, tente mais tarde",
      });
    }
  };

  useEffect(() => {
    getProdutos();
  }, []);

  return (
    <Container>
      <HeaderContent>
        <ConteudoTitulo>
          <Titulo>Listar Produtos</Titulo>
          <div>
            <span>Bem-vindo(a), {user?.nome}</span>
          </div>
        </ConteudoTitulo>
        <BotaoAcao>
          <Link to="/cadastrar">
            <ButtonSuccess>Cadastrar</ButtonSuccess>
          </Link>
          <ButtonDanger onClick={handleLogout}>Sair</ButtonDanger>
        </BotaoAcao>
      </HeaderContent>

      {status.type === "erro" ? (
        <AlertDanger>{status.mensagem}</AlertDanger>
      ) : (
        ""
      )}
      {status.type === "success" ? (
        <AlertSuccess>{status.mensagem}</AlertSuccess>
      ) : (
        ""
      )}

      <Table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Título</th>
            <th>Descrição</th>
            <th>Ações</th>
          </tr>
        </thead>
        <tbody>
          {Object.values(data).map((produto) => (
            <tr key={produto.id}>
              <td>{produto.id}</td>
              <td>{produto.titulo}</td>
              <td>{produto.descricao}</td>
              <td>
                <Link to={"/visualizar/" + produto.id}>
                  <ButtonPrimary>Visualizar</ButtonPrimary>
                </Link>{" "}
                <Link to={"/editar/" + produto.id}>
                  <ButtonWarning>Editar</ButtonWarning>
                </Link>{" "}
                <ButtonDanger onClick={() => apagarProduto(produto.id)}>
                  Apagar
                </ButtonDanger>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
    </Container>
  );
};
