import React from "react";
import { useHistory } from "react-router-dom";
import { FaBox, FaUserCog } from "react-icons/fa";
import { Container, MenuGrid, MenuCard, Title } from "./styles";

export const Menu = () => {
  const history = useHistory();

  const handlePerfilClick = () => {
    console.log("Clicou em Perfil");
    history.push("/alterar-senha");
  };

  return (
    <Container>
      <Title>Menu Principal</Title>
      <MenuGrid>
        <MenuCard onClick={() => history.push("/")}>
          <FaBox size={40} />
          <h3>Produtos</h3>
          <p>Gerenciar produtos do sistema</p>
        </MenuCard>

        <MenuCard onClick={handlePerfilClick}>
          <FaUserCog size={40} />
          <h3>Perfil</h3>
          <p>Gerenciar suas informações</p>
        </MenuCard>
      </MenuGrid>
    </Container>
  );
};
