import React from "react";
import { useHistory } from "react-router-dom";
import {
  Container,
  MenuCard,
  MenuGrid,
  Title,
  HeaderContainer,
} from "./styles";
import { FaBox, FaUserCog } from "react-icons/fa";

export const Menu = () => {
  const history = useHistory();

  return (
    <Container>
      <HeaderContainer>
        <Title>Menu Principal</Title>
        <button onClick={() => history.push("/login")} className="btn-sair">
          Sair
        </button>
      </HeaderContainer>
      <MenuGrid>
        <MenuCard onClick={() => history.push("/")}>
          <FaBox size={40} />
          <h3>Produtos</h3>
          <p>Gerenciar produtos do sistema</p>
        </MenuCard>

        <MenuCard onClick={() => history.push("/perfil")}>
          <FaUserCog size={40} />
          <h3>Perfil</h3>
          <p>Gerenciar suas informações</p>
        </MenuCard>
      </MenuGrid>
    </Container>
  );
};
