import React, { useState, useEffect } from "react";
import { Link, useHistory } from "react-router-dom";
import { useAuth } from "../../contexts/AuthContext";

import {
  Container,
  ConteudoTitulo,
  BotaoAcao,
  ButtonSuccess,
  Table,
  Titulo,
  ButtonPrimary,
  ButtonWarning,
  ButtonDanger,
  AlertSuccess,
  AlertDanger,
  HeaderContent,
} from "./styles";

export const Home = () => {
  const history = useHistory();
  const { user } = useAuth();
  const [usuarios, setUsuarios] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (!user) {
      history.push("/login");
      return;
    }

    const fetchUsuarios = async () => {
      try {
        setLoading(true);
        const response = await fetch("http://localhost:8000/usuarios");
        const data = await response.json();

        if (data.erro) {
          setError(data.mensagem);
          setUsuarios([]);
        } else {
          setUsuarios(data.usuarios || []);
          setError(null);
        }
      } catch (err) {
        setError("Erro ao carregar usuários: " + err.message);
        setUsuarios([]);
      } finally {
        setLoading(false);
      }
    };

    fetchUsuarios();
  }, [history, user]);

  if (loading) {
    return (
      <Container>
        <div>Carregando...</div>
      </Container>
    );
  }

  return (
    <Container>
      <ConteudoTitulo>
        <h1>Listar Usuários</h1>
        <BotaoAcao>
          <Link to="/cadastrar-usuario">
            <ButtonSuccess>Cadastrar</ButtonSuccess>
          </Link>
        </BotaoAcao>
      </ConteudoTitulo>

      {error && <AlertDanger>{error}</AlertDanger>}

      <Table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Nome</th>
            <th>E-mail</th>
            <th>Ações</th>
          </tr>
        </thead>
        <tbody>
          {usuarios.length > 0 ? (
            usuarios.map((usuario) => (
              <tr key={usuario.id}>
                <td>{usuario.id}</td>
                <td>{usuario.nome}</td>
                <td>{usuario.email}</td>
                <td>
                  <Link to={`/visualizar/${usuario.id}`}>
                    <ButtonSuccess>Visualizar</ButtonSuccess>
                  </Link>{" "}
                  <Link to={`/editar/${usuario.id}`}>
                    <ButtonSuccess>Editar</ButtonSuccess>
                  </Link>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="4" style={{ textAlign: "center" }}>
                Nenhum usuário cadastrado
              </td>
            </tr>
          )}
        </tbody>
      </Table>
    </Container>
  );
};

export default Home;
