import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";

import {
  Container,
  ConteudoTitulo,
  BotaoAcao,
  ButtonInfo,
  Titulo,
  ConteudoProd,
} from "./styles";

export const Visualizar = (props) => {
  const [data, setData] = useState([]);

  const [id] = useState(props.match.params.id);

  useEffect(() => {
    const getProduto = async () => {
      try {
        const response = await fetch("http://localhost:8000/produtos?id=" + id);
        if (!response.ok) {
          throw new Error(`Erro HTTP: ${response.status}`);
        }
        const responseJson = await response.json();
        setData(responseJson.produto);
      } catch (error) {
        console.error("Erro ao buscar produto:", error);
      }
    };
    getProduto();
  }, [id]);
  return (
    <Container>
      <ConteudoTitulo>
        <Titulo>Visualizar</Titulo>
        <BotaoAcao>
          <Link to="/">
            <ButtonInfo>Listar</ButtonInfo>
          </Link>
        </BotaoAcao>
      </ConteudoTitulo>
      <ConteudoProd>ID: {data.id}</ConteudoProd>
      <ConteudoProd>Título: {data.titulo}</ConteudoProd>
      <ConteudoProd>Descrição: {data.descricao}</ConteudoProd>
    </Container>
  );
};
