import React, { useState, useEffect } from "react";
import { Link, useHistory } from "react-router-dom";
import { useAuth } from "../../contexts/AuthContext";

import {
  Container,
  ConteudoTitulo,
  BotaoAcao,
  ButtonSuccess,
  Table,
  Titulo,
  ButtonPrimary,
  ButtonWarning,
  ButtonDanger,
  AlertSuccess,
  AlertDanger,
  HeaderContent,
} from "./styles";

export const Home = () => {
  const history = useHistory();
  const { signOut } = useAuth();
  const [data, setData] = useState({
    usuarios: [],
    loading: true,
    error: null,
  });
  const [status, setStatus] = useState({
    type: "",
    mensagem: "",
  });
  const [userName, setUserName] = useState("");

  const getProdutos = async () => {
    try {
      const response = await fetch("http://localhost:8000/produtos");
      const responseJson = await response.json();
      setData(responseJson.records);
    } catch (err) {
      setStatus({
        type: "erro",
        mensagem: "Erro ao carregar produtos: " + err.message,
      });
    }
  };

  const apagarProduto = async (id) => {
    try {
      if (!window.confirm("Deseja realmente apagar este produto?")) {
        return;
      }

      const response = await fetch(`http://localhost:8000/produtos/${id}`, {
        method: "DELETE",
        headers: {
          "Content-Type": "application/json",
        },
      });

      if (!response.ok) {
        throw new Error(`Erro HTTP: ${response.status}`);
      }

      const responseJson = await response.json();

      if (responseJson.erro) {
        throw new Error(responseJson.mensagem);
      }

      setStatus({
        type: "success",
        mensagem: responseJson.mensagem,
      });

      getProdutos(); // Atualiza a lista
    } catch (err) {
      setStatus({
        type: "erro",
        mensagem: "Erro ao apagar produto: " + err.message,
      });
    }
  };

  const handleLogout = () => {
    try {
      signOut();
      history.push("/login");
    } catch (error) {
      setStatus({
        type: "erro",
        mensagem: "Erro ao fazer logout: " + error.message,
      });
    }
  };

  useEffect(() => {
    try {
      const authUserStr = localStorage.getItem("@Authuser");
      console.log("Dados do @Authuser:", authUserStr); // Debug

      if (authUserStr) {
        const authUser = JSON.parse(authUserStr);
        console.log("Dados parseados:", authUser); // Debug

        if (Array.isArray(authUser) && authUser[0] && authUser[0].nome) {
          setUserName(authUser[0].nome);
          console.log("Nome definido:", authUser[0].nome); // Debug
        }
      }
    } catch (error) {
      console.error("Erro ao processar dados do usuário:", error);
    }
  }, []);

  useEffect(() => {
    getProdutos();
  }, []);

  console.log("userName atual:", userName); // Debug para ver o estado

  useEffect(() => {
    const fetchUsuarios = async () => {
      try {
        const response = await fetch("http://localhost:8000/usuarios");
        const jsonData = await response.json();

        if (jsonData.erro) {
          setData({
            usuarios: [],
            loading: false,
            error: jsonData.mensagem,
          });
        } else {
          setData({
            usuarios: Array.isArray(jsonData.usuarios) ? jsonData.usuarios : [],
            loading: false,
            error: null,
          });
        }
      } catch (err) {
        setData({
          usuarios: [],
          loading: false,
          error: "Erro ao carregar usuários: " + err.message,
        });
      }
    };

    fetchUsuarios();
  }, []);

  if (data.loading) {
    return <div>Carregando...</div>;
  }

  return (
    <Container>
      <HeaderContent>
        <ConteudoTitulo>
          <Titulo>
            {userName ? `Bem-vindo, ${userName}!` : "Bem-vindo!"}
            <br />
            Listar Produtos
          </Titulo>
          <div className="button-group">
            <Link to="/cadastrar">
              <BotaoAcao>Cadastrar</BotaoAcao>
            </Link>
            <ButtonDanger onClick={handleLogout}>Sair</ButtonDanger>
          </div>
        </ConteudoTitulo>
      </HeaderContent>

      {status.type === "erro" ? (
        <AlertDanger>{status.mensagem}</AlertDanger>
      ) : (
        ""
      )}
      {status.type === "success" ? (
        <AlertSuccess>{status.mensagem}</AlertSuccess>
      ) : (
        ""
      )}

      <Table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Título</th>
            <th>Descrição</th>
            <th>Ações</th>
          </tr>
        </thead>
        <tbody>
          {data.map((item) => (
            <tr key={item.id}>
              <td>{item.id}</td>
              <td>{item.titulo}</td>
              <td>{item.descricao}</td>
              <td>
                <Link
                  to={`/visualizar/${item.id}`}
                  className="btn btn-primary btn-sm"
                >
                  Visualizar
                </Link>{" "}
                <Link
                  to={`/editar/${item.id}`}
                  className="btn btn-warning btn-sm"
                >
                  Editar
                </Link>{" "}
                <button
                  onClick={() => apagarProduto(item.id)}
                  className="btn btn-danger btn-sm"
                >
                  Apagar
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
    </Container>
  );
};

export default Home;
